<?php

namespace App\Filament\Schemas;

/**
 * Icon sets for different template categories.
 * Each category has its own set of relevant icons for services and features.
 */
class IconSets
{
    /**
     * Get the icon options for a specific category.
     * Returns HTML options for use in Filament Select components.
     */
    public static function getServiceIcons(?string $categorySlug): array
    {
        return match ($categorySlug) {
            'kappers' => self::kappersIcons(),
            'aannemer' => self::aannemerIcons(),
            'zakelijk' => self::zakelijkIcons(),
            'horeca' => self::horecaIcons(),
            'automotive' => self::automotiveIcons(),
            default => self::defaultIcons(),
        };
    }

    /**
     * Get the default icon for a category.
     */
    public static function getDefaultIcon(?string $categorySlug): string
    {
        return match ($categorySlug) {
            'kappers' => 'scissors',
            'aannemer' => 'hammer',
            'zakelijk' => 'briefcase',
            'horeca' => 'utensils',
            'automotive' => 'car',
            default => 'star',
        };
    }

